import {Component, OnInit} from '@angular/core';
import {DeviceService} from "../../../service/device.service";
import * as pdfMake from "pdfmake/build/pdfmake";
import {CategoryService} from "../../../service/category.service";
import {CampusService} from "../../../service/campus.service";
import {FloorService} from "../../../service/floor.service";
import {RoomService} from "../../../service/room.service";

@Component({
  selector: 'app-device-list',
  templateUrl: './device-list.component.html',
  styleUrls: ['./device-list.component.css']
})
export class DeviceListComponent implements OnInit {
  deviceList: any;
  categoryList: any;
  campusList: any;
  nameCampusSelected = "";
  idCategory = '';
  id = '';
  brand = '';
  idCampus = '';
  idFloor = '';
  category: any;
  nameCategory = "";
  floorList: any;
  nameFloorSelected = "";
  roomList: any;
  idRoom = "";
  nameRoomSelected = "";
  result:any;
  statisticDeviceDTO: any

  constructor(
    private deviceService: DeviceService,
    private categoryService: CategoryService,
    private campusService: CampusService,
    private floorService: FloorService,
    private roomService: RoomService
  ) {
  }


  ngOnInit(): void {
    this.categoryService.findAll().subscribe(
      (data) => {
        this.categoryList = data;
        // console.log(this.categoryList)
        this.campusService.findAll().subscribe(
          (data) => {
            this.campusList = data;
            this.deviceService.getStatisticDevice().subscribe(
              (data) => {
                this.statisticDeviceDTO = data;
                // console.log(this.statisticDeviceDTO)
              }
            )
          }
        )
      })
  }


  idKeyUp($event: any) {
    this.id = $event.target.value;
    this.loadDeviceList()
  }


  categoryChange($event: any) {
    this.idCategory = $event.target.value;
    this.loadDeviceList()
  }

  brandKeyUp($event: any) {
    this.brand = $event.target.value;
    this.loadDeviceList()
  }

  campusChange($event: any) {
    this.nameCampusSelected = $event.target.selectedOptions[0].innerHTML;
    this.idCampus = $event.target.value;

    this.roomList = [];

    this.loadDeviceList()
    this.floorService.findFloorByCampus_Name(this.nameCampusSelected).subscribe(
      (data) => {
        this.floorList = data
      }
    )
  }

  floorChange($event: any) {
    this.nameFloorSelected = $event.target.selectedOptions[0].innerHTML;
    this.idFloor = $event.target.value;
    this.loadDeviceList()
    this.roomService.findRoomByFloor_Name(this.nameFloorSelected).subscribe(
      (data) => {
        this.roomList = data;
      }
    )
  }

  roomChange($event: any) {
    this.nameRoomSelected = $event.target.selectedOptions[0].innerHTML;
    this.idRoom = $event.target.value;
    this.loadDeviceList()
  }


  private loadDeviceList() {
    this.deviceService.searchDevice(this.id, this.idCategory, this.brand, this.idCampus, this.idFloor, this.idRoom).subscribe(
      (data) => {
        this.statisticDeviceDTO = data;
        // console.log(this.statisticDeviceDTO);
      }, () => {
      }, () => {
        //Hien thi Category trong Report
        if (this.idCategory != '') {
          this.categoryService.findByID(this.idCategory).subscribe(
            (data) => {
              this.category = data;
              this.nameCategory = this.category.name;
            }
          )
        } else {
          this.nameCategory = '';
        }
      }
    )
  }

  printListDevice() {
    this.result=''
    let today = new Date();
    let body = [[{text: "Order", alignment: 'center'}, {text: "ID", alignment: "center"}, {
      text: "Category",
      alignment: "center"
    }, {text: "Use Date", alignment: "center"}, {text: "Brand", alignment: "center"}, {
      text: "Model",
      alignment: "center"
    }, {
      text: "Serial",
      alignment: "center"
    }, {
      text: "Note",
      alignment: "center"
    }, {text: "Configuration", alignment: "center"}, {text: "Room", alignment: "center"}, {
      text: "Floor",
      alignment: "center"
    }]];

    for (let i = 0; i < this.statisticDeviceDTO.deviceList.length; i++) {
      let bodyTemp = [];
      bodyTemp.push({text: i + 1, alignment: "center"})
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].id, alignment: "center"});
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].category.name, alignment: "center"});
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].useDate, alignment: "center"})
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].brand, alignment: "center"})
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].model, alignment: "center"})
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].serial, alignment: "center"})
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].note, alignment: "center"})

      let configuration = "";
      for (let j = 0; j < this.statisticDeviceDTO.deviceList[i].configurationList.length; j++) {
        configuration += this.statisticDeviceDTO.deviceList[i].configurationList[j].type.name + ": " + this.statisticDeviceDTO.deviceList[i].configurationList[j].detail + ', '
      }
      // Xoa day phau cuoi chuoi
      configuration = configuration.substring(0, configuration.length - 2);

      bodyTemp.push(configuration)
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].room.name, alignment: "center"})
      bodyTemp.push({text: this.statisticDeviceDTO.deviceList[i].room.floor.name, alignment: "center"})

      // @ts-ignore
      body.push(bodyTemp)
    }

    for (let j = 0; j < this.statisticDeviceDTO.statisticDeviceList.length; j++) {
      console.log(this.statisticDeviceDTO.statisticDeviceList[j])
      this.result += this.statisticDeviceDTO.statisticDeviceList[j] + ', '
      console.log(this.result);
    }

    let docDefinition = {
      margin: [0, 0, 0, 0],
      pageSize: 'A4',
      pageOrientation: 'landscape'
      ,
      content: [
        {
          alignment: 'center',
          columns: [
            {
              columns: [
                {},
                {
                  image: 'data:image/png;base64,',
                  width: 100
                },
                {}
              ],
              width: "30%"
            },
            [
              {
                text: 'LIST OF DEVICES',
                bold: true,
                fontSize: 24,
                width: "70%",
                alignment: 'center',
              },
              {
                alignment: 'center',
                text: "ID: " + this.id + " - Category: " + this.nameCategory
              },
              {
                alignment: 'center',
                text: "Campus: " + this.nameCampusSelected + " - Floor: " + this.nameFloorSelected + " - Room: " + this.nameRoomSelected

              },
              {
                alignment: 'right',
                text: "Date: " + today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate(),
                fontSize: 10,
                italics: true
              }
            ]
          ]
        },
        {
          alignment: 'left',
          // text: "Statistic: " + this.result,
          text: "Statistic: " + this.result.substring(0, this.result.length - 2),
          fontSize: 8,
          bold: true,
          italics: true
        },
        {
          table: {
            widths: [50, 50, 50, 50, 50, 50, 50, 50, '*', 50, 50],
            // widths: [50,50,50],
            body: body
          },
          fontSize: 8
        }
      ]
    }

    // @ts-ignore
    pdfMake.createPdf(docDefinition).open();
  }


}
